import './App.css';
import { useDispatch, useSelector } from 'react-redux';
import { decrByPayloadAction, dellProduct, incrByPayloadAction } from './store/countReduser';


function App() {

  let products = useSelector((store) => store.count);
  console.log(products);
  let dispatch = useDispatch();

  return (
    <div>
        <div>

          <h1>Корзина</h1>
          {products.map((el, index)=>
            <div key={index}>
                <p>{el.title}</p>
                <div>
                <button onClick={()=> dispatch(incrByPayloadAction(el.id))}>+</button>
                  <button onClick={()=>dispatch(decrByPayloadAction(el.id))}>-</button>
                  <p>{el.count}</p>

                </div>
                <button onClick={()=>dispatch(dellProduct(el.id))}>Удалить продукт</button>
            </div>)}

        </div>
    </div>
  );
}

export default App;
