import React from "react";
import s from './index.module.css'
import { useDispatch, useSelector } from "react-redux";
import { addProductAction, decrByPayloadAction, dellProductAction, incrByPayloadAction } from "../../store/countReduser";
import { AiOutlineArrowsAlt } from "react-icons/ai";

function Products() {
    let products = useSelector((store) => store.count);
    console.log(products);
    let dispatch = useDispatch();

  return (
    <div className={s.cart}>
<div className={s.btns}>
          <button className={s.close_button} onClick={() => dispatch(dellProductAction(el.id))}></button>
          <button className={s.hide}></button>
          <button className={s.zoom}><AiOutlineArrowsAlt className={s.btn_arowe}/></button>
        </div>

    <div className={s.productsList}>
      <button className={s.addProduct} onClick={() => dispatch(addProductAction(prompt('Write name product.')))}>
        Add new product
      </button>
      {products.map((el, index) => (
        <div key={index}
        className={s.products}>
          <p>{el.title}</p>
          <div className={s.container_Btn}>
            <div className={s.triggersBtn}>
                <button className={s.plus} onClick={() => dispatch(incrByPayloadAction(el.id))}>
                +
                </button>
                <p>{el.count}</p>
                <button className={s.minus} onClick={() => dispatch(decrByPayloadAction(el.id))}>
                -
                </button>

            </div>
           <button className={s.delete} onClick={() => dispatch(dellProductAction(el.id))}>
                X
            </button>
          </div>

        </div>
      ))}
    </div>
    </div>
  );
}

export default Products;
