import React from "react";
import s from './index.module.css'
import { useDispatch, useSelector } from "react-redux";
import { addProductAction, decrByPayloadAction, dellProductAction, incrByPayloadAction } from "../../store/countReduser";

function Products() {
    let products = useSelector((store) => store.count);
    console.log(products);
    let dispatch = useDispatch();

  return (
    <div className={s.products}>
      <h1>Корзина</h1>
      <button onClick={() => dispatch(addProductAction(prompt()))}>
        Add new product
      </button>
      {products.map((el, index) => (
        <div key={index}>
          <p>{el.title}</p>
          <div>
            <button onClick={() => dispatch(incrByPayloadAction(el.id))}>
              +
            </button>
            <p>{el.count}</p>
            <button onClick={() => dispatch(decrByPayloadAction(el.id))}>
              -
            </button>
          </div>
          <button onClick={() => dispatch(dellProductAction(el.id))}>
            Удалить продукт
          </button>
        </div>
      ))}
    </div>
  );
}

export default Products;
