const defaultState = [
    {id: 1, title: 'Велосипед', count: 5},
    {id: 2, title: 'Самокат', count: 4},
    {id: 3, title: 'Гантели', count: 7},
    {id: 4, title: 'Ракетки', count: 1}
]

    const DERC_COUNT = 'DERC_COUNT';
    const INCR_COUNT = 'INCR_COUNT';
    const DELL_PRODUCT = 'DELL_PRODUCT'
    const RESET_CART = 'RESET_CART';

    export const countReduser = (state = defaultState, action) => {
        switch (action.type) {
            case DERC_COUNT:
                const dectTargetProduct = state.find((el) => el.id === action.payload);
                dectTargetProduct.count = dectTargetProduct.count -1 ;
                return state.filter((el) => el.count !== 0);

            case INCR_COUNT:
                 const incrTargetProduct = state.find((el) => el.id === action.payload);
                //  incrTargetProduct.count = incrTargetProduct.count + 1;
                return state.map((el) => el.count = 26 ? {...state, count: incrTargetProduct.count + 1} : <p key={el.id}>Превышено максимальное значение</p>)

            case DELL_PRODUCT:
                return state.filter((el)=> el.id !== action.payload)



            case RESET_CART:
                return {...state, count: 0};
            default:
                return state;
        }
    };

    export const decrByPayloadAction = (payload) => ({
        type: DERC_COUNT, payload
    });
    export const incrByPayloadAction = (payload) =>({
        type: INCR_COUNT, payload
    });
    export const dellProduct = () => ({type:DELL_PRODUCT});
