const defaultState = [
    {id: 1, title: 'Велосипед', count: 5},
    {id: 2, title: 'Самокат', count: 4},
    {id: 3, title: 'Гантели', count: 7},
    {id: 4, title: 'Ракетки', count: 1}
]

    const DERC_COUNT = 'DERC_COUNT';
    const INCR_COUNT = 'INCR_COUNT';
    const RESET_COUNT = 'RESET_COUNT';

    export const countReduser = (state = defaultState, action) => {
        switch (action.type) {
            case DERC_COUNT:
                state.filter((el, index))
                return {...state, count: state.count - action.payload};
            case INCR_COUNT:
                return {...state, count: state.count + action.payload};
            case RESET_COUNT:
                return {...state, count: 0};
            default:
                return state;
        }
    };

    export const decrByPayloadAction = (payload) => ({
        type: DERC_COUNT, payload
    });
    export const incrByPayloadAction = (payload) =>({
        type: INCR_COUNT, payload
    });
